import {Link} from 'react-router-dom';
import React, { useState } from 'react';
import './Nav.css';
import CreategroupModal from '../modals/create-group/creategroup_modal';
import MypageModal from '../modals/my-page/mypage_modal';

//로고 이미지 파일 경로
import logoImage from './logo.png';
import creategroupImage from './creategroupImage.png';
import userImage from './user.png';

function Nav() {
  const [isModalOpen, setIsModalOpen] = useState(false);

  const handleOpenModal = () => {
    setIsModalOpen(true);
  };

  const handleCloseModal = () => {
    setIsModalOpen(false);
  };

  return (
    <div>
      <div className="navbar">
        {/* 로고 이미지를 추가 */}
        <img src={logoImage} alt="Logo" className="navbarLogo" />

        {/* 선을 추가 */}
        <hr className="navbarLine" />

        {/* 그룹 생성하기 */}
        {/* 이미지를 클릭하면 팝업이 열리도록 이벤트를 추가 */}
        <img src={creategroupImage} alt='CreateGroup' className="navbarLogo" onClick={handleOpenModal} />
        {/* 팝업 모달 */}
        <CreategroupModal isOpen={isModalOpen} onClose={handleCloseModal} />

        {/* 마이페이지 */}
        <img src={userImage} alt='User' className="creategroupusetImage" onClick={handleOpenModal} />
        <MypageModal isOpen={isModalOpen} onClose={handleCloseModal} />

        {/* 메뉴들 */}
        <Link className="navbarMenu" to={'/'}>Main</Link>
        <Link className="navbarMenu" to={'/login'}>Login</Link>

      </div>
    </div>
  )
}

export default Nav;
